import "./index.css";
import logo from "./images/logo.svg";
import { useState } from "react";

export default function App() {
  const [bill, setBill] = useState(0);
  const [numPeople, setNumPeople] = useState(0);
  const [custom, setCustom] = useState("");
  const [percentage, setPercentage] = useState("");

  function handleClick(e) {
    setPercentage(Number(e.target.value));
  }

  return (
    <div className="container">
      <div className="logo">
        <img src={logo} alt="logo" />
      </div>
      <div className="card">
        <Bill
          bill={bill}
          setBill={setBill}
          numPeople={numPeople}
          setNumPeople={setNumPeople}
          custom={custom}
          setCustom={setCustom}
          percentage={percentage}
          setPercentage={setPercentage}
          onSelectPercentage={handleClick}
        />
        <Result />
      </div>
    </div>
  );
}

function Bill({
  bill,
  setBill,
  numPeople,
  setNumPeople,
  custom,
  setCustom,
  onSelectPercentage,
  percentage,
}) {
  return (
    <div className="bill">
      <div className="bill-info separator">
        <label>Bill</label>
        <input
          type="text"
          value={bill}
          onChange={(e) => setBill(Number(e.target.value))}
        />
      </div>
      <div className="tip-info separator">
        <label>Select Tip %</label>
        <div className="inputs" value={percentage}>
          <input type="text" value="5" readOnly onClick={onSelectPercentage} />
          <input type="text" value="10" readOnly onClick={onSelectPercentage} />
          <input type="text" value="15" readOnly onClick={onSelectPercentage} />
          <input type="text" value="25" readOnly onClick={onSelectPercentage} />
          <input type="text" value="50" readOnly onClick={onSelectPercentage} />
          <input
            className="special"
            type="text"
            placeholder="Custom"
            value={custom}
            onChange={(e) => setCustom(Number(e.target.value))}
          />
        </div>
      </div>
      <div className="people-info separator">
        <span className="number">
          <label>Number of Peaple</label>
          <span
            className="message"
            style={numPeople === 0 ? { display: "block" } : { display: "none" }}
          >
            Can't be zero
          </span>
        </span>
        <input
          style={numPeople === 0 ? { outline: "2px solid #ff0000d9" } : {}}
          type="text"
          value={numPeople}
          onChange={(e) => {
            if (!bill || !percentage) return;
            setNumPeople(Number(e.target.value));
          }}
        />
      </div>
    </div>
  );
}

function Result({
  bill,
  setBill,
  numPeople,
  setNumPeople,
  custom,
  setCustom,
  onSelectPercentage,
  percentage,
}) {
  let tipAmount = (percentage / bill) * numPeople;
  return (
    <div className="result">
      <div className="separator tip-result">
        <div className="parent">
          <label>Tip Amount</label>
          <span>/ person</span>
        </div>
        <input type="text" value={!bill ? "$0.00" : tipAmount} readOnly />
      </div>

      <div className="total-result">
        <div className="parent">
          <label> Total</label>
          <span>/ person</span>
        </div>
        <input type="text" value="$0.00" readOnly />
      </div>

      <button>Reset</button>
    </div>
  );
}
